float distanceToPerimeter(vec2 p, vec2 bounds, float radius)
{
	return length(max(abs(p) - bounds, 0.0)) - radius;
}


float cubicPulse( float c, float w, float x )
{
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord - iResolution.xy * 0.5;
    float radius = 6.0;
	vec2 bounds = vec2(170.0 - radius, 120.0 - radius);
	const float w = 8.0;
    float d = distanceToPerimeter(p, bounds, radius);
    float innerStep = smoothstep(0.25, 1.75, d);
    float outerStep = smoothstep(w - 0.75, w + 0.75, d);
    float c = 0.3 + innerStep * 0.7 - outerStep;
    float inBorder = cubicPulse(w * 0.5 + 0.5, w * 0.2, d); 
	vec2 texCoord = vec2(fragCoord/ iResolution.xy);
    vec4 bgColor = texture(iChannel0, texCoord);

    const float highlightSpeed = 0.66;
    const float highlightWidth = 0.13;

    float angle = (atan(p.y, p.x * bounds.y / bounds.x) / 3.1415 + 1.0) * 0.5;
    angle = mod(angle + iTime * highlightSpeed, 1.0);
    angle = cubicPulse(highlightWidth, highlightWidth, angle) * inBorder;
    vec4 frameColor = vec4(1.0, 0.2, 0.5, c);
    frameColor.rgb = mix(frameColor.rgb, vec3(1.0, 0.8, 0.9), angle);
    fragColor = vec4(mix(bgColor.rgb, frameColor.rgb, frameColor.a), 1.0);
}
