void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 bg =  vec3(0.4, 0.32, 0.4);
    vec3 color1 = vec3(0.8, 0.3, 0.4);
    vec3 color2 = vec3(0.3, 0.4, 0.8);
    vec3 color3 = vec3(0.4, 0.8, 0.3);
    vec3 color4 = vec3(0.2, 0.6, 0.7);
    vec3 color5 = vec3(0.6, 0.6, 0.8);

    vec3 pixel = bg;

    float t = mod(iTime/5.0, 1.0);

    if(uv.y > t -1.0 && uv.y < t - 0.9){
    	pixel = color1;
    } 
    else if(uv.y > t - 0.8 && uv.y < t - 0.7){
    	pixel = color2;
    } 
    else if(uv.y > t - 0.6 && uv.y < t - 0.5){
    	pixel = color3;
    } 
    else if(uv.y > t - 0.4 && uv.y < t - 0.3){
    	pixel = color4;
    } 
    else if(uv.y > t - 0.2 && uv.y < t - 0.1){
    	pixel = color5;
    }  
    else if(uv.y > t && uv.y < 0.1 + t){
    	pixel = color1;
    }
    else if(uv.y > 0.2 + t && uv.y < 0.3 + t){
    	pixel = color2;
    }
    else if(uv.y > 0.4 + t && uv.y < 0.5 + t){
    	pixel = color3;
    }
    else if(uv.y > 0.6 + t && uv.y < 0.7 + t){
    	pixel = color4;
    }
    else if(uv.y > 0.8 + t && uv.y < 0.9 + t){
    	pixel = color5;
    }

   	fragColor = vec4(pixel, 1);

}
