void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord/iResolution.x;
    vec2 uv = fragCoord/iResolution.x - vec2(0.5, 0.5*iResolution.y/iResolution.x);

    uv = vec2(sign(uv.x) * uv.x * uv.x, sign(uv.y) * uv.y * uv.y);

    float rt = 3.141592 * sin(iTime/2.);
    mat2 rmt = mat2(cos(rt), -sin(rt), sin(rt), cos(rt));
    uv = rmt * uv;

    float modval = 0.025 + 0.015 * sin(iTime);
    uv = mod(uv, modval);

    vec3 col = vec3(0.);

    vec2 center = vec2(modval/2.);
    float radius = (0.9*modval/2.);

    float dd = distance(uv, center);

	vec2 cv = rmt * uv0;
	if (dd >= radius && dd <= radius + 0.002)
        col = vec3(distance(cv, vec2(1.)), 
                   distance(cv, vec2(0.5)), 
                   distance(cv, vec2(0.)));

    fragColor = vec4(col,1.0);
}
