vec4 getColor(vec2 uv)
{
	if (uv.x >= 400.0)
	{
		float g = (uv.x - iResolution.x / 2.0) / iResolution.x;
		return vec4(g, g, g, 1.0);
	}
	else
	{
		float g = 1.0 - (uv.x + iResolution.x / 2.0) / iResolution.x * 1.5;
		return vec4(g, g, g, 1.0);
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	float angleToAdd = iTime;
	vec2 anchor = iResolution.xy / 2.0;
	float angle = atan(uv.y - anchor.y, uv.x - anchor.x);
	float newAngle = angle + angleToAdd;
	vec2 newUv;
	newUv.x = length(uv - anchor) * cos(newAngle);
	newUv.y = length(uv - anchor) * sin(newAngle);
	fragColor = getColor(newUv);
}
