void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = vec4(1, 1, 1, 1);
    float flagX = uv.x * 1.2 - 0.1;
    float flagY = (uv.y + sin((flagX - iTime) * 10.0) * 0.05 * flagX) * 1.2 - 0.1;
    flagX = flagX - flagY * 0.05;
    if (flagX > 0.0 && flagX < 1.0 && flagY > 0.0) {
        if (flagY < 1.0 / 6.0) {
            fragColor = vec4(1, 0, 1, 1);
        } else if (flagY < 2.0 / 6.0) {
            fragColor = vec4(0, 0, 1, 1);   
        } else if (flagY < 3.0 / 6.0) {
            fragColor = vec4(0, 1, 0, 1);   
        } else if (flagY < 4.0 / 6.0) {
            fragColor = vec4(1, 1, 0, 1);   
        } else if (flagY < 5.0 / 6.0) {
            fragColor = vec4(1, 0.5, 0, 1);   
        } else if (flagY < 6.0 / 6.0) {
            fragColor = vec4(1, 0, 0, 1);   
        }
    }
}
