vec2 zmul(vec2 a, vec2 b){return mat2(a,-a.y,a.x)*b;} // z * z 
vec2 zinv(vec2 a){return vec2(a.x, -a.y) / dot(a,a);} // 1 / z

const float AA = 2.;

void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(0);

	vec2 si = iResolution.xy;

    for( float m=0.; m<AA; m++ )
    for( float n=0.; n<AA; n++ )
    {
        vec2 o = vec2(m,n) / AA - .5;
        vec2 uv = ((g+o)*2.-si)/min(si.x,si.y) * 2.2;
        vec2 z = uv;
        vec2 c = vec2(.924,0);
		c.y = sin(iTime) * .03;
        float it = 0.;
        for (int i=0;i<100;i++)
        {
            z = zinv( .48 * zmul(z, z) - c);
			if( dot(z,z) > 8. ) break;
            it++;
        }

		f += .5 + .5 * sin( 3. + it * 2. + vec4(1,0,1,1));
    }

    f /= AA * AA;
}
