void mainImage( out vec4 f, in vec2 g )
{
	vec2 u = (g+g-iResolution.xy)/iResolution.y;
    u = fract(u) - .5;
    float r = .8, d = step(length(u),r);
    for (int i = 0; i < 9; i++)
    {
        r *= .5 + .1 * sin(float(i) + iTime);
        if (mod(float(i), 2.) == 0.)
        {
        	d -= step(length(u+vec2(0, r)), r);
        	d -= step(length(u+vec2(r, 0)), r);
        	d -= step(length(u-vec2(0, r)), r);
        	d -= step(length(u-vec2(r, 0)), r);
        }
        else
        {
            d += step(length(u+vec2(0, r)), r);
        	d += step(length(u+vec2(r, 0)), r);
        	d += step(length(u-vec2(0, r)), r);
        	d += step(length(u-vec2(r, 0)), r);
        }

    }
	f = vec4(d);
}
