void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
	float bpm = 1.0 / 60.0 * 185.0;
    float pi = 3.1415926535;
    float st = sin(t * bpm * pi);


    float intense = 
        texture(iChannel0, vec2(0.4, 0.25)).x + 
        texture(iChannel0, vec2(0.6, 0.25)).x + 
        texture(iChannel0, vec2(0.8, 0.25)).x + 
        texture(iChannel0, vec2(0.9, 0.25)).x;

    nuv = nuv * vec2(st * intense, 1.0 * st * 100.0);
	nuv += texture(iChannel1, nuv).xy * intense;

    fragColor.xyz = vec3(0.0, 0.0, 0.6 * st);
	fragColor.xyz += 
        vec3(0.0, 0.2, 0.3) * abs(nuv.x) * pow(intense, 3.0) * 4.0;

    fragColor.xyz *= max(0.5, 1.0 - length(nuv * 0.2) * pow(abs(st), 12.0));

    fragColor.xyz += texture(iChannel2, uv).xyz * 0.002 * (1.0 - length(nuv)) + texture(iChannel0, vec2(0.2, 0.25)).x * 0.14;
}
