#define CUTOFF 0.23

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv.x *= 1000.0;
	uv.y *= 550.0;

	// blob setup	

	vec2 blob1pos = vec2(250.0, 180.0);
	float blob1size = 350.0;
	vec2 blob2pos = vec2(450.0, 200.0);
	float blob2size = 300.0;
	vec2 blob3pos = vec2(600.0, 260.0);
	float blob3size = 400.0;

	// movement

	blob1pos.x += sin(iTime*3.0) * 70.0;
	blob1pos.y += sin(iTime*1.2) * 90.0;
	blob2pos.x += sin(iTime*2.0) * 150.0;
	blob2pos.y += sin(iTime*3.0) * 20.0;
	blob3pos.y += sin(iTime*1.4) * 100.0;
	blob3pos.x += sin(iTime*1.7) * 100.0;

	// calc

	float blob1dist = clamp(distance(blob1pos, uv),0.0, blob1size);
	float blob1influence = clamp( 1.0 - blob1dist/blob1size, 0.0, 1.0 );

	float blob2dist = clamp(distance(blob2pos, uv),0.0, blob2size);
	float blob2influence = clamp( 1.0 - blob2dist/blob2size, 0.0, 1.0 );

	float blob3dist = clamp(distance(blob3pos, uv),0.0, blob3size);
	float blob3influence = clamp( 1.0 - blob3dist/blob3size, 0.0, 1.0 );

	// finished

	float intensity = (pow(blob1influence,6.0) + pow(blob2influence,6.0) + pow(blob3influence,6.0));
	intensity = (clamp(intensity,CUTOFF, CUTOFF + 0.0125)-CUTOFF) * 80.0;

	fragColor = vec4(intensity) - vec4(blob1influence,blob2influence,blob3influence,1.0);
}
