float PI = 3.1415926;
#define AA 1.0
#define AA_SAMPS 8
#define SPEED .05
#define ITERATIONS 128.0
//mmalex's random functions
float srand(vec2 a) { return fract(sin(dot(a,vec2(1233.224,1743.335)))); }
vec2 rand(vec2 r) { return fract(3712.65*r+0.61432); }

float easeWithSteps(float t, float steps)
{
	float frac = 1.0 / steps;	
	float eT = mod(t, frac);
	float x = eT / frac;
	return t - eT + frac * x*x*x*(x*(x*6.0 - 15.0) + 10.0); // fancy smoothstep (see wikipeed)
}

float map(float x, float xmin, float xmax, float ymin, float ymax)
{
	// miss u processing
	return clamp(ymin + (x - xmin) / (xmax - xmin) * (ymax - ymin), ymin, ymax);
}


vec2 iterate( vec2 v, vec2 c)
{
	return vec2(v.x * v.x - v.y * v.y + c.x, 2.0 * v.x * v.y + c.y);
}

vec4 colorAtCoord(vec2 uv, float t)
{
	uv =uv.xy / iResolution.y;
	vec2 p = uv -vec2(.5 * iResolution.x / iResolution.y,.5);
	float sT =  .5 + .5 * sin(-PI/2.0 + easeWithSteps(t, 2.0) * PI * 2.0);
	float scale = 2.000 - 1.999*sT;//10.0 - sT * 9.99;
	//scale = .001;
	p.x -= .2;
	p *= scale;
	// framing
	p.x += .2799 * sT;
	p.y += -.01025 * sT;
	float phaseR = iMouse.x / iResolution.x * PI;
	float phaseC = iMouse.y / iResolution.y * PI;


	vec2 z = vec2(0.0);

	float iters = 0.0;
	float limit = 10.0;
	float dist = 0.0;
	float dist2 = 0.0;
	float dx = 0.0;
	float dy = 0.0;
	for ( float i = 0.0; i < ITERATIONS; i+=1.0)
	{
		z = iterate(z, p);
		if (abs(z.x + z.y) > 4.0)
//		if (max(abs(z.x), abs(z.y)) > 2.0) 
		{
			iters = i;

			dx = z.x - p.x;
			dy = z.y - p.y;
			dist = abs(dx + dy);
			dist2 = max(abs(z.x), abs(z.y));
			break;
		}
	}

	vec4 color = vec4(sqrt(dist) / (10.0));//iters / ITERATIONS;
	color.g = (dist2) / 100.0;
	color.b = iters / 10.0; // beep boop nfi what i'm doing
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// assume 60fps
	float t = iTime * SPEED;
	vec4 c=vec4(0.);

	// mmalex's AA/blur code.
	vec2 aa=vec2( srand(fragCoord.xy), srand(fragCoord.yx) );
	t+=1.0/60.0/float(AA_SAMPS)*aa.x*SPEED;	

	for (int i=0;i<(AA_SAMPS);i++) {
		aa=rand(aa);
		c+=colorAtCoord(fragCoord.xy+aa,t);
		t+=1.0/60.0/float(AA_SAMPS)*SPEED;
	}	
	c=sqrt(c/float(AA_SAMPS));
	//c.g=sqrt(c/float(AA_SAMPS));
	//c.b=sqrt(c/float(AA_SAMPS));	
	fragColor = c;
}
