void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float r = 0.4;
    float d = length(uv);
    float c = smoothstep(d,d+0.02,r);
	fragColor = vec4(vec3(c,0.5*sin(iTime)+0.5,0.),1.0);
}
