// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

/*
   Astroid facts:
  ----------------
	- (a) Also called the tetracuspid because it has four cusps.
	- (b) Curve can be formed by rolling a circle of radius a/4 on the inside of a circle of radius a.
	- (c) The curve can also be formed as the envelop produced when a line segment is moved with
		  each end on one of a pair of perpendicular axes (glissette)
*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      48
#define eps_            0.1

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 8.);

    // t -> [0., 5.]
    float t = abs(sin(time_)*5.);

    // Astroid traces
    float dtrace = smoothstep(0.008, 0.001, trace(p, t));
    float dtrace_s0 = smoothstep(0.16 + sin(time_)*.1, 0.001, trace(p, t));

    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, 1.-ax);
    c = mix(c, white, org);

    c = mix(c, white, dtrace);
    c = mix(c, vec3(0.5, 1., 1.1) + vec3(abs(sin(time_)), abs(sin(time_)), 0.), dtrace_s0);
    c.b += dtrace_s0;

    o = vec4(sqrt(c), 1.);
}

vec2 parametric(float t) {
	float a = 4.;
    return vec2(a*cos(t)*cos(t)*cos(t), a*sin(t)*sin(t)*sin(t));
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = min(d, distance(p, parametric(t-e)));
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}
