void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec2 nuv = uv * 2.0 - 1.0;
    nuv.x /= iResolution.y / iResolution.x;

    float t = iTime;
	float bpm = 1.0 / 60.0 * 90.0;
    float trill = texture(iChannel0, vec2(0.0, 0.25)).x;
    float tick = texture(iChannel0, vec2(0.9, 0.25)).x;
    float waah = texture(iChannel0, vec2(0.3, 0.25)).x;

    trill = pow(trill - 0.35, 4.0) * 12.0;
    tick = pow(tick - 0.0, 2.0) * 4.5;
    waah = pow(waah - 0.1, 2.0) * 4.0;

    float g0 = step(0.5, fract(uv.x * 50.0 + t * 8.0));
    float g1 = step(0.5, fract(uv.y * 50.0));

    float uv0a = t;
    vec2 uv0r = mat2(cos(uv0a), -sin(uv0a), sin(uv0a), cos(uv0a)) * nuv;

    float c0 = step(1.5 + sin(uv0r.x * 8.0 + t * 2.0) * 0.08 * waah * 2.0, 1.0 - uv0r.y);

    float s0 = pow(length(nuv * 1.1), 8.0);

    s0 *= length(vec2(sin(t * 1.2), cos(t * 2.4)));

    float cl0 = pow(sin(length(vec2(nuv * 20.0)) - t * 12.0), 4.0) * 0.2;

    float col0 = g0 + g1 - s0 * 0.5;
    float col1 = c0;
    float col2 = s0;

    fragColor.xyz += vec3(0.3 + tick * 0.1);
    fragColor.xyz += vec3(0.2 - waah * 0.5, 0.0, 0.0) * col0;
    fragColor.xyz += vec3(1.4, 0.3 + waah * 0.5, 0.0) * col1;
    fragColor.xyz += vec3(0.6, 0.2 + tick * 0.1, 1.5) * (1.0 - abs(c0 - s0));
    fragColor.xyz += vec3(1.0) * pow(cl0, 3.0 - trill * 1.5) * (10.0 + trill * 5.5);

}
