void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 120.0;
    float pi = 3.14159265359;
    float bt = t * bpm;
    float btp = bt * pi;
    float btp4 = btp / 4.0;

    vec3 c = vec3(0.0);

    float mt = btp4;
    mat2 rm = mat2(cos(mt), -sin(mt), sin(mt), cos(mt));

    nuv = rm * vec2(nuv.x * sin(abs(nuv.x) + btp4), abs(nuv.y)*1.0);

    float ts = 1.0 / 32.0;
    float freqE = 146.83;
    float g0 =
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqE * 1.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqE * 2.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqE * 3.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqE * 4.0) / 23.0), 0.25)).x;
    float freqB = 110.0;
    float g1 =
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqB * 1.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqB * 2.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqB * 3.0) / 23.0), 0.25)).x +
        texture(iChannel0, vec2(1.0 / 512.0 * ((freqB * 4.0) / 23.0), 0.25)).x;

    g0 /= 4.0;
    g1 /= 4.0;

    g0 = max(0.0, g0 - 0.45);
    g1 = max(0.0, g1 - 0.45);

    g0 = pow(g0, 0.7);
    g1 = pow(g1, 0.7);

    float w0 = texture(iChannel0, vec2(uv.x, 0.75)).x;
    float m0 = texture(iChannel0, vec2(uv.x, 0.25)).x;
    float m0n = texture(iChannel0, vec2(abs(nuv.x), 0.25)).x;

    m0n = pow(m0n, 3.0) * 20.0;

    float f0 = pow(m0n, 4.0);
    float f1 = w0 * (1.0 - pow(abs(nuv.y), 0.1));
    float f2 = 1.0 - pow(abs(w0 - uv.y), 1.0 - g0) * 0.3;

    float fwave = g0 * g1 * 2.5 * pow(abs(sin(nuv.x + btp4 + 1.3*f2)), 12.0);

    c.xyz = vec3(abs(f2 * fwave * pow(abs(nuv.y), 1.4) * 4.0 + m0n * -fwave));
    c.xyz += vec3(0.2 * pow(1.0 - f0, 0.0));

    c.z += abs(g0 * g1);
    c.z += max(0.0, f2 * 2.0) * 0.5;
    c.y += max(0.0, f2 * 2.0) * 2.0 * pow(g0, 3.0);

    c.xyz *= 1.0 - length(abs(nuv.y));

    fragColor.xyz = c;
}
