void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   	vec2 uv = fragCoord.xy / iResolution.xy; 

    float holeSize = iMouse.x;

    fragColor = texture ( iChannel0, uv);

    if(holeSize > iResolution.x/2.0){   

   	 fragColor.xyz.rgb = pow((1.0 - distance(fragCoord.xy,iResolution.xy*0.5)/holeSize ),2.0) *fragColor.xyz.rgb;
    }else{
       fragColor.xyz.rgb = (1.0 - distance(fragCoord.xy,iResolution.xy*0.5)/ 230.0 ) *fragColor.xyz.rgb; 
    }

}
