//* FarbriceNeyret2 and pthextract 113c
void mainImage( out vec4 f, vec2 g )
{
    g -= f.xy=iResolution.xy/2.;

   	f = vec4(g *= g += f.a = pow(.3 - dot(g/=f.y,g), .2), f.aa) * f.a;
}/**/

