void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;
    for (float i = 1.; i < 11.; i ++){
        if (uv.x > (i - 1.) / 10. && uv.x < i / 10.) {
            uv.y -= 1. * abs(cos(time + i / 10.)) * abs(sin(time + i / 2.));
                fragColor = vec4(uv.y, abs(cos(time * 0.5)), 1.0-uv.y, uv.x);
        } 
    }
}
