//based on code originally by bonniem: https://www.shadertoy.com/view/4dsGzH

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 wave_color = vec3(0.0);
    vec2 uv = fragCoord.xy / iResolution.xy;
	float wave_width;

    for(int i = 0; i < 10; i++) {
		uv  = -1.0 + 2.3 * uv;
		uv.y += (sin(iTime/2.0 + float(i))*sin(uv.x + iTime ));
    	wave_width = 1.0 / (15.0 * uv.y);
    	wave_color += vec3(wave_width, wave_width * 2.0, wave_width * 1.5);
    }


	fragColor = vec4(wave_color, 1.0);

}
