#define PI 3.14159265359
#define RADIUS 0.01
mat3 rot(vec3 z,float a){
	float c=cos(a);
	float s=sin(a);
	float b=1.0-c;
	return mat3(
		b*z.x*z.x+c,b*z.x*z.y-z.z*s,b*z.z*z.x+z.y*s,
		b*z.x*z.y+z.z*s,b*z.y*z.y+c,b*z.y*z.z-z.x*s,
		b*z.z*z.x-z.y*s,b*z.y*z.z+z.x*s,b*z.z*z.z+c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 look=vec3(0.0,0.0,0.0);
    vec3 eye=vec3(5.0*sin(iTime/10.0),0.0,5.0*cos(iTime/10.0));
    vec3 direction=normalize(look-eye);
    vec2 pos=-0.5+fragCoord.xy/iResolution.xy;
    vec3 up=vec3(0.0,1.0,0.0);
    vec3 horizontal=normalize(cross(direction,up));
    vec3 vertical=normalize(cross(horizontal,direction));
    pos.x*=iResolution.x/iResolution.y;
    float fov=5.0/PI/2.0;
    direction*=rot(vertical,fov*pos.x);
	direction*=rot(horizontal,fov*pos.y);
    vec3 ray=eye;
    for(int i=0;i<64;i++){
        float r=length(ray);
        vec3 axis=normalize(cross(ray,direction));
        direction*=rot(axis,RADIUS/r);
        ray+=0.1*direction;
    }
    fragColor=texture(iChannel0,direction);
}
