void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Current fragment's normalized position on screen.
	vec2 uv = fragCoord.xy / iResolution.xy;

    // iMouse's xy values contain the (last) mouse drag position. Simple.
    // iMouse's zw values contain the (last) click position, and are
    // signed negative if the mouse button is up.

    // Determine if mouse button is pressed.
    bool isMouseDown = iMouse.z > 0.;

    // iMouse.zw values are negative if the mouse button is not pressed.
    vec2 mouseDragStart = iMouse.zw * (isMouseDown ? 1. : -1.);

    float distToMouse = distance(fragCoord.xy, iMouse.xy);
    float distToDragStart = distance(fragCoord.xy, mouseDragStart.xy);

    if (uv.x < 0.5) {
        // Left side of screen shows iMouse.zw as solid red+green colour (as 'normalized' mouseDragStart.xy) 
        fragColor = vec4(
            mouseDragStart.xy / iResolution.xy,
            0.,0.);
    } else {
        // Right side of screen shows iMouse.xy as solid red+green colour (current drag location)
        fragColor = vec4(
            iMouse.xy / iResolution.xy,
            0.,0.);
    }

    // Mouse button indicator in middle of screen. Large, faded, solid circle if up, empty if down.
    float distToScreenCenter = distance(fragCoord.xy, vec2(0.5,0.5) * iResolution.xy);
    fragColor += 0.3 * vec4(smoothstep(30.5,29.5,distToScreenCenter) - (isMouseDown ? 0. : 1.) * smoothstep(26.5,25.5,distToScreenCenter) );

    // Mouse drag location. Small white circle.
    fragColor += vec4( smoothstep(5.5,4.5, distToMouse) );

    // Mouse drag start location. Small white ring.
    fragColor += vec4( smoothstep(10.5,9.5, distToDragStart) - smoothstep(8.5,7.5, distToDragStart) );

}
