void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy;
    float t = iTime;
    float v = .0;
	vec4 ax = vec4(10.,233.,123.,421.);
	vec4 ay = vec4(12., 212., 33., 488.);
	for (int i = 0; i < 4;i++ ) {
		vec2 ad = (sin(t*.1 + vec2(ax[i],ay[i])) + 1.0) * iResolution.xy/2.;
		v+= sin(distance(xy,ad)/3.);
	}
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 offset = vec2((v+4.)/8.)*.06;
    fragColor = texture(iChannel0, uv+offset);
}
