const float CUTOFF = 10.0;
const float MAX_ITERATIONS = 50.0;
const float HALF_PI = 3.14159 / 2.;
const float TWO_PI = 4. * HALF_PI;
const float ZOOM_MODIFIER = TWO_PI / 40.;

const vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);

vec3 hsv2rgb(vec3 c)
{
	// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 center = ((iMouse.xy / iResolution.xy) - 0.5) * 2.;
    vec2 pt = (((fragCoord.xy / iResolution.xy) - 0.5) * 2.);
    vec2 c =  center - (pt / (exp(sin(iTime * ZOOM_MODIFIER - HALF_PI) * 5. + 4.)));
    vec2 z = c;

    for(float i = 0.0; i < MAX_ITERATIONS; i+=1.0) {

    	z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

        if(dot(z,z) > CUTOFF) {
        	float tone = i / MAX_ITERATIONS;
            fragColor = vec4(hsv2rgb(vec3(tone,tone,1. - tone)),1.0);
            //fragColor = vec4(tone,1.0 - tone,tone,1.0);
            return;
        }
    }

    fragColor = vec4(1.0,1.0,1.0, 1.0);
}
