#define PI 3.141592653589793238462643383279
#define NLAYERS 6
#define MAXZ 0.8
#define SPEED 0.25

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float n = 1.0 / float(NLAYERS);
	vec4 frag = vec4(0,0,0,0);
	vec2 texcoord = fragCoord.xy / iResolution.xy - 0.5;
	for (int i=0; i<NLAYERS; i++)
	{
		float p = fract(fract(iTime * SPEED) + float(i) * n);
		float c = 1.0 - abs(p-0.5)*2.0; // sin(p*PI);
 		vec2 uv = 0.5 + texcoord * (1.0-p*MAXZ);
 		frag += texture(iChannel0, uv) * c * 0.5;
	}
	fragColor = (frag * frag);
}
