void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float iTime = 17.58;			// iFrozenTime 17.58s or 12.58s

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2  q = uv - vec2( 0.5, 0.5 );
    float dt = sqrt( abs( mod( iTime, 1.5 ) - 0.75 ) ) *  2.0;

    vec3 col = vec3 ( 1.0, 0.4, 0.1 );

    float amp  =  0.05 + dt * 0.1;
    float freq =  7.0;
    float warp = 12.0 * q.x + 4.0 * dt;

    float r = 0.2 + dt * amp * cos( atan( q.x, q.y ) * freq - warp);
    float leaves = smoothstep( r, r + 0.2 + dt * amp, length( q ) );

    col *= leaves;

    r = 0.0025;

    float distort_leaves =  1.0 - smoothstep( r, r + 0.1 + dt * amp, abs( q.x ) );
    float distort_trunk  = -0.5 - smoothstep( 0.02, 0.019, q.y );

    col *= 1.0 - ( distort_leaves ) * ( distort_trunk );

    fragColor = vec4( col, 1.0 );
}
