void mainImage( out vec4 O, in vec2 U )
{
	vec2 v = U.xy / iResolution.y;
    float t = iTime*.132;
    float k = (cos(-t * 0.2) + 0.3) * .8;
    vec3 f = vec3(0.1, 0.3, 0.2)*3.1;
    vec3 s = vec3(1.3, 0.14, 2.12);
    vec3 a = vec3(0.223, 0.21, 0.14);
    vec3 l = vec3(2.12, 4.6, 7.5) * .2;
    vec3 w = vec3(sin(v.x * l - t * s) * a + k);
    O = smoothstep(w, w + f, v.yyy).xyzz;
}
