const float iterations = 20.0;
float noise(in vec2 position) {
    return fract(sin(dot(position.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float interpolate(float a, float b) {
    return a + ( b - a ) * ( ( 1.0 - a ) / ( b - a ) );
}

// Value Noise courtesy of Book of Shaders
// https://thebookofshaders.com/11/
float noise2d(vec2 uv) {

    vec2 pos = floor(uv);
    vec2 fractional = fract(uv);

    // four corners
    float a = noise(pos);					// bottom left
    float b = noise(pos + vec2(1., 0.));	// bottom right
    float c = noise(pos + vec2(1., 1.));	// top right
    float d = noise(pos + vec2(0., 1.));	// top left

    vec2 intermix = smoothstep(0., 1., fractional);

    float value = mix(a, b, intermix.x);
    value += (d - a) * intermix.y * (1.0 - intermix.x);
    value += (c - b) * intermix.x * intermix.y;

    return value;
}

// Fractal noise courtesy of iq
// https://www.shadertoy.com/view/XdXGW8
float fractalNoise2d(vec2 uv) {

    mat2 rotate = mat2(1.6, 1.2, -1.2, 1.6);

    float value = 0.5 * noise2d(uv);
    uv *= rotate;
    value += 0.25 * noise2d(uv);
    uv *= rotate;
    value += 0.125 * noise2d(uv);
    uv *= rotate;
    value += 0.0625 * noise2d(uv);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
//    uv.x *= 1.5; // for teh circles!

    float dist = 1.0;

    for(float i = 1.0; i <= iterations; i++) {

        vec2 noisePosition = vec2( i / iterations );
        noisePosition += vec2(iTime / 250.0, 0.0);
        noisePosition *= 20.0;

        float x = fractalNoise2d(noisePosition);
        float y = fractalNoise2d(noisePosition + 1.0);

        vec2 texelPos = vec2(-2., -1.0) + vec2(x, y) * vec2(4.5, 2.5);

        dist = min(dist, length(texelPos - uv));
    }
    fragColor = vec4(floor(fract((1.0 - clamp(dist * 2.0, 0.0, 1.0) * 8.0)) * 2.4));
    // uncomment for the raw distance field
    //fragColor = vec4(1.0 - clamp(dist * 2.0, 0.0, 1.0));
}
