float whangHashNoise(uint u, uint v, uint s)
{
    //return fract(sin(float(s + (u*1080u + v)%10000u) * 78.233) * 43758.5453);

    uint seed = (u*1664525u + v) + s;

    seed  = (seed ^ 61u) ^(seed >> 16u);
    seed *= 9u;
    seed  = seed ^(seed >> 4u);
    seed *= uint(0x27d4eb2d);
    seed  = seed ^(seed >> 15u);

    float value = float(seed) / (4294967296.0);
    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    uint animation = 0u;
#if 0
    animation = uint(iTime*12345.0);
#elif 1
    animation = uint(iTime);
    animation = (animation*12345u)%256u;
#endif

    float noise = whangHashNoise(uint(fragCoord.x), uint(fragCoord.y), animation);
    fragColor = vec4(noise, noise, noise, 0);
}
