void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;  
    vec4 texColor = texture(iChannel0,xy);  

    float dif1 =  abs(texColor.r -  texColor.g)  ;
    float dif2 =  abs(texColor.r -  texColor.b)  ;
    float dif3 =  (sin(iTime) * dif1);
    float dif4 =  (sin(iTime) * dif2); 
    texColor.g += dif3 ;
    texColor.b += dif4;
    fragColor = texColor; 
}
