void mainImage(out vec4 O,vec2 U){vec2 u = U / iResolution.xy; O = texture(iChannel0, u); float f = mod(iChannelTime[1] / 11.92, 3.), o = texture(iChannel1, vec2(u.y, 0.)).x; abs(fract(f) - u.x) < 3e-3 ? f < 1. ? O.r = o : f < 2. ? O.g = o : O.b = o : o;}

