// Check buffer A for the code
void mainImage( out vec4 o, vec2 u )
{
	o = texelFetch(iChannel0, ivec2(u),0);
}

#define r(x,y) + texelFetch(iChannel0, ivec2(u)+ivec2(x,y),0)
void mainImage( out vec4 o, in vec2 u )
{
	o = ( 
        max(4. - .3*abs(float(int(u.x)/4^int(u.y)/4) - (sin(iTime/4.-3.)+1.) * 128.),0.)
    	r(64,0)
    	r(-64,0)
    	r(0,64)
    	r(0,-64)
    )*0.2;
}
