{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = M_PI * 2.0 * sin(iTime / 10.0);
    vec2 c = vec2(iResolution.x, iResolution.y) * 0.5;
    vec2 c0 = c + vec2(
        0.25 * iResolution.x * cos(t * M_PI),
        0.25 * iResolution.y * sin(t * M_PI)
    );
    vec2 c1 = c + vec2(
        0.25 * iResolution.x * cos(t * M_PI * 0.20),
        0.25 * iResolution.y * sin(t * M_PI * 2.13)
    );
    float d0 = distance(fragCoord, c0) * 0.25;
    float d1 = distance(fragCoord, c1) * 0.25;
    float t0 = trunc(0.75 + sin(d0 * M_PI / 20.0));
    float t1 = trunc(0.75 + sin(d1 * M_PI / 11.0));
    float tr = float(int(t0) ^ int(t1));
    vec3 decay = vec3(
        0.30 + 0.15 * sin(iTime),
        0.31 + 0.15 * sin(iTime * 1.13),
        0.32 + 0.15 * sin(iTime * 1.23)
    );
	fragColor = vec4(
        vec3(tr) + texture(iChannel0, fragCoord / iResolution.xy).rgb * (1.0 - decay),
        1.0
    );
}
