void mainImage( out vec4 o, vec2 u )
{
    vec2 R = iResolution.xy,
         g = (u*2.-R)/R.x;
    u = vec2(atan(g.y,g.x)/3.14-.5,length(g)-.5);
    float t = fract(iDate.w);
	o += min(length(g+vec2(0,pow(t*2.-max(t*4.-2.,0.), 2.)*.27-.5))-.04, u.y+floor(u.x*2.+t)*.2-t*.2+.4)*length(R*.2)-o;
}
