#define PI 3.141592653589793238462643383279502884197169399375105820974944592307816
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;


	float wave =  texture( iChannel0, vec2(0.0,1.0)).x;
	float wave2 = texture( iChannel0, vec2(0.0,.1)).x;

	uv = vec2(uv.x + sin(iTime)*.3, uv.y + cos(iTime)*.3);

	float dist = (length(uv) * (wave2 * sin(iTime) + 1.0) );

	float angle = ((atan(uv.x,uv.y)+PI)/(PI*2.) + sin(iTime) * .04);

	float expt = .45;
	wave = sin(wave) * .2;
	float r = (sin((pow(dist,expt)*100. * sin(wave))-(abs(sin(angle*PI*40.)*2.3)+abs(sin(angle*PI*sin(iTime) * 80.)*0.2)))+1.)/2.;
	float g = (sin((pow(dist,expt)*70. * sin(iTime * .3))-(abs(sin(angle*PI*40.)*2.3)+abs(sin(angle*PI*80.)*0.2)))+1.)/2.;
	float b = (sin(((pow(dist,expt)*70.)+1.4)-(abs(sin(angle*PI*40.)+abs(sin(angle*PI*80.)*0.2))*2.3))+1.)/2.;

	fragColor = vec4(r,g, b, 1.);

}
