void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-1.+2.*fragCoord.xy/iResolution.xy)*vec2(iResolution.x/iResolution.y,1.);
	vec4 scene = vec4(0.0);

	// parameters
	float baseRadius = 0.02;
	float blur = 0.0065;	
	vec4 background = vec4(0,0,0,0);

	for(float i=0.;i<100.;i++) {
		// animate
		vec2 lCenter,rCenter,mCenter,noise;
		lCenter.x = sin(.001+i*.5+iTime)*(.75);
		lCenter.y = sin(.001+i*.5+iTime)*(0.5)+cos(i*.5+iTime)*.2;
		rCenter.x = -1.*sin(.001+i*.5+iTime)*(.75);
		rCenter.y = sin(.001+i*.5+iTime)*(0.5)+cos(i*.5+iTime)*.2;
		mCenter.x = 0.-cos(iTime+i)*-.2;
		mCenter.y = sin(iTime+i)*(0.8);
		noise.x = 0.-cos(.001+.8*i*.2+iTime)*(1.5);
		noise.y = 0.-cos(.001+i+iTime)*1.2;

		float radius = baseRadius*2./4.+cos(i*.5+iTime)*baseRadius/4.;

		float distL = length(uv-lCenter);	
		float distR = length(uv-rCenter);	
		float distM = length(uv-mCenter);
		float bg = length(uv-noise);

		vec4 rightRing = vec4(.91,.4,.91,smoothstep(0.,blur,distR-radius));
		vec4 leftRing = vec4(.91,.4,.91,smoothstep(0.,blur,distL-radius));
		vec4 middleRing = vec4(.91,.4,.91,smoothstep(0.,blur,distM-radius));
		vec4 bgCircles = vec4(.21,.4,.21,smoothstep(0.,blur,bg-radius/2.));

		scene += mix(bgCircles, background,bgCircles.a);		
		scene += mix(rightRing,background,rightRing.a);
		scene += mix(leftRing,background,leftRing.a);
		scene += mix(middleRing,background,middleRing.a);
		scene.rgb = min(scene.rgb,vec3(.91,.4,.91));
	}

	fragColor = scene;	
}
