void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float angleInput = atan(fragCoord.y, fragCoord.x);
    float radius = 0.001 * sqrt(fragCoord.x * fragCoord.x + fragCoord.y + fragCoord.y);
    float ang = iTime;
    uv = uv + vec2(sin(angleInput + ang) * radius, cos(angleInput + ang) * radius);
    fragColor = texture(iChannel0, uv);
}
