#define t iTime

float pulse(float cn, float wi, float x)
{
	return 1.-smoothstep(0., wi, abs(x-cn));
}

float hash11(float n)
{
    return fract(sin(n)*43758.5453);
}

vec2 hash22(vec2 p)
{
    p = vec2( dot(p,vec2(127.1, 311.7)), dot(p,vec2(269.5, 183.3)));
	return fract(sin(p)*43758.5453);
}

vec2 field(in vec2 p)
{
	vec2 n = floor(p);
	vec2 f = fract(p);
	vec2 m = vec2(1.);
	vec2 o = hash22(n)*0.17;
	vec2 r = f+o-0.5;
	float d = abs(r.x) + abs(r.y);
	if(d<m.x)
    {
		m.x = d;
		m.y = hash11(dot(n,vec2(1., 2.)));
	}
	return vec2(m.x,m.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-0.5;
	uv.x *= iResolution.x/iResolution.y*0.9;
	uv *= 4.;

	vec2 p = uv*.01;
	p *= 1./(p-1.);

	uv.y += sin(t*.5)*2.2+t;
	uv.x += cos(t*.5)*0.8;
	vec2 buv = uv;

	float rz = 0.;
	vec3 col = vec3(0.);
	for(float i=1.; i<= 50.; i++)
	{
		vec2 rn = field(uv);
		uv -= p*(i*.5-20.)*0.2;
		rn.x = pulse(0.7,.02, rn.x+rn.y*.15);
		col += rn.x*vec3(.4+sin(rn.y*sin(t*10.)), cos(rn.y)*0.2*cos(t*10.)+.2,sin(rn.y)*0.3+.2);
	}

	fragColor = vec4(col,1.0);
}
