#define PI 3.1415926535

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy - iResolution.xy / 2.0;
    float phi = atan(p.y + 1e-6, p.x);

    float t = sin(iTime/5.)*4.;
	float fin = floor(phi * t / PI + 0.5);
    float phi_fin = fin * PI / t;

    vec2 dir = vec2(cos(phi_fin), sin(phi_fin));
    float l = dot(dir, p);

    float ivr = 30.0;
    float seg = l / ivr;

    float w = sin(floor(seg)*.1 - iTime) * 0.45 + 0.5;
    float c = (w / 2.0 - abs(fract(seg) - 0.5)) * ivr;

    fragColor = vec4(c, c, c, 1);
}
