void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
	O = texture(iChannel0,U);
}

// inspired by "self-tiling shapes" by JP Delahaye, /Pour La Science/ #470 p 76 Dec,2016

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define CS(a) vec2(cos(a),sin(a))
#define PI 3.14159265359
#define imod(i,m) (i-(i)/(m)*(m))
void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
	U /= R;

    if (imod(iFrame,30) > 0 ) { 	O = texture(iChannel0,U); return; }

    float e=.05, s = .866025; // sqrt(3)/2
    U.x -= .5;
    //U *= vec2(2,s);
    U *= vec2(2,2.*R.y/R.x);

    if (imod(iFrame,30*7)==0) {  // initial pentagon
        O = vec4( U.y > e && U.y < s-e  &&  .5*U.y <  s*(1.-abs(U.x))-e ); 
        return; 
    }

    O-=O;
    // --- IFS transform
    if (U.y<s/2. &&  s*(.5-abs(U.x)) >.5*U.y)                // central child
        U*= 2., O++;
    else if ( -s*U.x >.5*U.y )                               // left child
        U -= s*CS(PI*5./6.), U *= 2.* rot(-PI*2./3.), O.r++; 
    else if ( s*U.x >.5*U.y)                                 // right child
        U -= s*CS(PI/6.),    U *= 2.* rot( PI*2./3.), O.b++; 
    else                                                     // top child
        U -= s*CS(PI/2.), U *= 2.* rot( PI ), O.g++;   

    U.x = .5 + .5*U.x; U.y *= s;  // not sure why the U.y *= s
    //U = fract(U);
    if (U.y<0.) { O-=O; return; }
    O = texture(iChannel0,U) * (.7+.3*O);
}
