#define PI 3.1415926535897932384626433832795
#define half_PI 1.570796326794896619231321692

float max_dist = distance(vec2(1.0, 0.5), vec2(0.5, 0.5));
float max2_dist = distance(vec2(0.0, 0.0), vec2(0.5, 0.6));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float dist = distance(uv, 
                 vec2(0.5+sin(iTime/3.)*.12, 0.5+sin(iTime/2.)*.12));
    float dist1 = dist / max_dist;
    float dist2 = dist / max2_dist;

    float a = mod((atan(
        uv.x - 0.5+sin(iTime/3.)*.16, 
        uv.y - 0.5+sin(iTime/2.)*.16)
                   - (PI * sin(iTime/7.))) / 
                  PI*(1.35+atan(sin(iTime/2.)*1.37)), 0.971111+cos(iTime/3.1)*.1);
 //   if (uv.x < 0.5 ) a = 1.0 - a;

    float c0 = abs(
          texture(iChannel0, vec2(a / 3.0, 0.25)).x * 3.
        + texture(iChannel0, vec2((1.-a) / 3.0, 0.25)).x
        ) * .3
        ;
    float c1 = texture(iChannel0, vec2(1.0 - ((a) / 2.5), 0.25)).x;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    if (dist1 <= c0) {
        fragColor = vec4(
            c0*c0+a/2.5,
            1.1-cos(dist*3.5),
           // 0.3+sin(dist*2.8)-c0,
           (
            (0.5+cos(-dist1*15.+fract(c1+a*(1.0+cos(iTime*4.)))*2.*PI+c0+c1)*.45) 
               + 
            (0.5-cos(dist1*15.+fract(c1+a*(1.0+cos(iTime*4.)))*2.*PI+c0+c1)*.45) 
          //     +  (.3+cos(a/15.+dist*2.8)*.3)
           )*0.35,
            1.0);
    } else {
        fragColor.rgb = mix(fragColor.rgb/dist2, vec3(dist2, c1, 0.4), dist2 * c1);
    }
}
