vec4 returnV(vec4 value)
{
    vec4 valuetoreturn = ((value*(0.22f*value+0.1f*0.3f)+0.2f*0.01f)/(value*(0.22f*value+0.3f)+0.2f*0.3f)) - 0.01f/0.3f;
    return valuetoreturn;
}


float returnF(float value)
{
    float valuetoreturn = ((value*(0.22f*value+0.1f*0.3f)+0.2f*0.01f)/(value*(0.22f*value+0.3f)+0.2f*0.3f)) - 0.01f/0.3f;
    return valuetoreturn;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv += vec2(sin(iTime)*.1, cos(iTime*3.)*.2);
	fragColor = vec4(uv,.5+0.5*sin(iTime),1.0);
    vec4 color = texture(iChannel0, uv*vec2(.2, 1.));
    uv += vec2(sin(iTime)*.1, cos(iTime*3.)*.2);
    color += texture (iChannel1,  uv);

   	fragColor += texture(iChannel0, uv * .2+vec2(.3) )* vec4(uv,0.5+0.5*sin(iTime),1.0);
 	fragColor += color;   
    //fragColor = fragColor*0.5;
    fragColor = returnV(fragColor) / returnF(11.2f);
}
