void upsideDown( out vec4 fragColor, in vec2 fragCoord );
void zoom( out vec4 fragColor, in vec2 fragCoord );
void pinch( out vec4 fragColor, in vec2 fragCoord );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    pinch( fragColor, fragCoord );
}

void pinch( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 m = iMouse.xy / iResolution.xy;
    if( m.x <= 0.0 && m.y <= 0.0)
        m = vec2(0.5);
    vec2 direction = uv;
    direction.x -= m.x;
    direction.y -= m.y;
    direction.x *= iResolution.x / iResolution.y;
    uv *= length(direction);
    vec4 color = texture(iChannel0, uv);
    fragColor = vec4(color.rgb, 1.0);
}
