void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(texture(iChannel0, uv).rgb,1.0);
}

vec2 mainSound( in int samp, float time )
{
    float Hz = 60.0;
    float Scan = 0.01250;
    float timeMod = fract(time*Hz)*0.1250;
    float timeScan = floor(time*Hz)/Hz*Scan*0.125;

    //float timeScan = floor(time*Hz/Scan)/Hz*Scan;

    return cos(timeMod*timeScan)*sin(vec2( texture(iChannel0,vec2(timeMod,timeScan)  ).rg*4.0-2.0 )*150.0);
}
