float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame == 0) {
        float r = rand(fragCoord);
        if(r < 0.5) {
            fragColor = vec4(vec3(0.0, 1.0, 0.0), 1.0);
        }
        else {
            fragColor = vec4(vec3(0.0), 1.0);
        }
    }
    else {
        fragColor = texture(iChannel1, fragCoord/iResolution.xy);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float left = fragCoord.x - 1.;
    if(left < 0.) {
        left = iResolution.x;
    }
    float right = fragCoord.x + 1.;
    if(right > iResolution.x) {
        right = 0.;
    }
    float up = fragCoord.y + 1.;
    if(up > iResolution.y) {
        up = 0.;
    }
    float down = fragCoord.y - 1.;
    if(down < 0.) {
        down = iResolution.y;
    }

    vec4 left_v = texture(iChannel0, vec2(left, fragCoord.y)/iResolution.xy);
    vec4 leftup_v = texture(iChannel0, vec2(left, up)/iResolution.xy);
    vec4 leftdown_v = texture(iChannel0, vec2(left, down)/iResolution.xy);
    vec4 right_v = texture(iChannel0, vec2(right, fragCoord.y)/iResolution.xy);
    vec4 rightup_v = texture(iChannel0, vec2(right, up)/iResolution.xy);
    vec4 rightdown_v = texture(iChannel0, vec2(right, down)/iResolution.xy);
    vec4 up_v = texture(iChannel0, vec2(fragCoord.x, up)/iResolution.xy);
    vec4 down_v = texture(iChannel0, vec2(fragCoord.x, down)/iResolution.xy);

    int count = int(left_v.y + leftup_v.y + leftdown_v.y + right_v.y + rightup_v.y + rightdown_v.y + up_v.y + down_v.y);
    if(count == 3) {
        fragColor = vec4(vec3(0.0, 1.0, 0.0), 1.0);
    }
    else if(count == 2) {
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    }
    else {
        fragColor = vec4(vec3(0.0), 1.0);
    }
}
