void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 coords = vec2(fragCoord.xy / iResolution.xy);
	coords -= vec2(0.5, 0.5);
//	coords /= vec2((3.0+sin(iTime))/2.0);
	coords /= 3.0*(1.0 - distance(coords, vec2(0.0, 0.0)));

	fragColor = texture(iChannel0, coords+vec2(0.5, 0.5));
}
