//thanks to iq's work
#define ambientAmount 0.5
#define shininess 300.0

vec3 ldir;

float Eta = 0.66; // Ratio of indices of refrection
//fresnel F
float F = 0.04195;//((1.0-Eta)*(1.0-Eta))/((1.0+Eta)*(1.0+Eta));
float FresnelPower = 5.0;

float heartH(vec2 p)
{
    //animate
    float tt = mod(iTime, 1.5)/1.5;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss -= ss*0.2*sin(tt*6.2831*3.0)*exp(-tt*4.0);

    //shape
    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);
    float h = abs(a);
    float d = (13.0*h-22.0*h*h+10.0*h*h*h)/(6.0-5.0*h);

    return smoothstep(-.01,1.01,d-r);
}

vec3 light(vec2 p,vec3 col)
{
    //reflection
    vec2 d = vec2(0.,.003);
    float d1 = heartH(p-d.xy)-heartH(p+d.xy);
    float d2 = heartH(p-d.yx)-heartH(p+d.yx);
    vec3 n1 = vec3(0.,d.y,d1);
    vec3 n2 = vec3(d.y,0.,d2);
    vec3 n = normalize(cross(n1,n2));
    float diff = max(0.,dot(ldir,n))*.6;
    vec3 r = reflect(vec3(.0,.0,1.0),ldir);
    float spec = pow(max(0.,dot(r,n)),25.)*0.4;

    vec3 diff_col = diff*col;
    vec3 spec_col = spec*col;
    vec3 reflect_col = diff_col + spec_col;

    //refraction
    vec3 v = vec3(0.,0.,2.) - vec3(p,heartH(p));
    v = normalize(v);
    vec3 lfrac = refract(ldir,n,Eta);
    float ratio = F+(1.0-F)*pow((1.0-dot(v,n)),FresnelPower);
    float interval = -1.0*(heartH(p)/lfrac.z);
    p = interval*p;
    vec3 refract_col = vec3(1.0,0.8,0.7-0.07*p.y)*(1.0-0.25*length(p));

    return mix(refract_col,reflect_col,ratio);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

	p.y -= 0.25;

    // background color
    vec3 bcol = vec3(1.0,0.8,0.7-0.07*p.y)*(1.0-0.25*length(p));

    // animate
    float tt = mod(iTime,1.5)/1.5;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss -= ss*0.2*sin(tt*6.2831*3.0)*exp(-tt*4.0);
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);


    // shape
    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);
    float h = abs(a);
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

	// c  olor
	float s = 1.0-0.5*clamp(r/d,0.0,1.0);
	s = 0.75 + 0.75*p.x;
	s *= 1.0-0.25*r;
	s = 0.5 + 0.6*s;
	s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );
	vec3 hcol = vec3(1.0,0.5*r,0.3)*s;

    vec3 col = mix( bcol, hcol, smoothstep(-.01,0.4,d-r) );

    //lighting
    vec3 lightPos = vec3(0.8,.2,2.0); 
    ldir = normalize(vec3(p,heartH(p))-lightPos);
    col = light(p,col)*.01+col*.9;

    fragColor = vec4(col,1.0);
}
