void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 color;
    vec2 pos = fragCoord.xy;

    float r = 0.0;
    float g = 0.0;
    float b = 0.0;


    for(float i=0.0; i<10.0; i++){

        vec2 center = vec2(sin(iTime*2.0+i*10.0)*iResolution.x*0.01*i+iResolution.x*0.5, 
                        cos(iTime*2.0+i*10.0)*iResolution.y*0.01*i+iResolution.x*0.25);

        float dist = distance (pos, center);     

        r+= sin(dist/10.0+iTime);
    	g+= cos(dist/10.0-iTime*1.1);
    	b = 0.2-(r+g)*0.1;
        r+=b*0.2;
    }

    color= vec4 (r, g, b, 1.0);

	fragColor = color;
}
