void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = floor(uv *50.0f)/50.0f; //modify this line for pixel size
    uv += vec2(sin(iTime)*.1, cos(iTime*3.)*.2);
	fragColor = vec4(uv,.5+0.5*sin(iTime),1.0);
    vec4 color = texture(iChannel0, uv*vec2(.2, 1.));
    uv += vec2(sin(iTime)*.1, cos(iTime*3.)*.2);
    color += texture (iChannel1,  uv);

   	fragColor += texture(iChannel0, uv * .2+vec2(.3) )* vec4(uv,0.5+0.5*sin(iTime),1.0);
 	fragColor += color;   
    fragColor = fragColor*0.3;
}
