void mainImage( out vec4 f, in vec2 w )
{
    f=texture(iChannel0, w/iResolution.xy);
}

void mainImage( out vec4 f, in vec2 w )
{
    // t - total ray distance
    // x - the distance function
    // o - the distance between each point and 0
    float T=iTime, t=0., x, o;
	vec2 R = iResolution.xy, u = vec2((2.*w-R)/R.y);
    vec3 r = vec3(u, 1), d=normalize(vec3(u,-1)), p, l=r-r, c; // Basic ray march set up
    for (float i = 1.; i > 0.; i -= 0.01)
    {

        p = r + d * t;
        //p.xy += sin(p.z + T)*.4;
        p.z -= iTime;
        x = length(mod(p*.5, 2.)-1.)-.5; // Distance function
        t += x;
        if (x < 0.005 || t > 30.) break;
        o = distance(p, vec3(0.));
        c = sin(vec3(o, 1.-o, 1./o) + t);
        c += l = texture(iChannel0, w/R-.01).rgb*.5; // l - The framebuffer
    }
    f.rgb=c;
}
