vec2 S(float f)
{
    return vec2(iTime * f);
}

float sat(float f)
{
    return clamp(f, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

	float t = iTime;
    float pi = 3.1415926535;

    vec2 uv0 = uv * 0.3 + vec2(0.0, 0.0);
    vec2 uv1 = uv * 0.3 + vec2(0.33, 0.33);
    vec2 uv2 = uv * 0.3 + vec2(0.66, 0.66);

    float n0 = texture(iChannel0, uv0 + S(0.012)).x;
    float n1 = texture(iChannel0, uv1 + S(0.001)).x;
    float n2 = texture(iChannel0, uv2 + S(0.001)).x;

    float c0 = sin(n0 * 20.0);
    float c1 = sin(n1 * 10.0);
    float c2 = sin(n2 * 10.0);

    vec3 fg = vec3(
        abs(pow(nuv.y, 4.0) * 2.0 * sin(nuv.x * 12.0 + t * 0.5) * 0.3),
        abs(c1 * n0),
    	abs(c2 * n1)
    );

    fg.yz *= abs(cos(uv.x * -20.0)) + cos(t * -20.0) * 0.1;

    float c = sin(texture(iChannel0, vec2(uv.x, t * 0.002)).x * 1.0);
    float t0 = max(0.0, t - 2.0);
    float t1 = sat(t0 * 0.2);

    t1 = smoothstep(0.0, 1.0, t1 - abs(sin(t * 1.0)) * 0.08) * 0.75;

    vec3 col = vec3(0.0);
    vec3 curtain = vec3(1.0, 0.0, 0.0) * c;

    col = vec3(fg);
    col = mix(col, curtain, vec3(step(t1, abs(nuv.x))));

    fragColor.xyz = col;
}
