void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 data = texture(iChannel0,uv);

    fragColor.rgb = abs(data.rgb);
    fragColor = fragColor/(fragColor+iMouse.x/iResolution.x*80.0);
}

float rand(vec2 co, float offset){
    return fract(sin(dot(co.xy+iTime*offset ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;
    fragColor = texture(iChannel0,fragCoord.xy/iResolution.xy);

    float angle = rand(uv,1.0)*3.14159254*2.0;
    //angle*=0.15;
    //angle += iTime/32.0 + sin(iTime/55.0);

    vec2 sample_offset = vec2(cos(angle), sin(angle));
    vec2 sample_spot = uv+sample_offset*32.0;

    vec4 sampled = texture(iChannel0, fract( sample_spot / iResolution.xy));


    float delta = sampled.r - fragColor.r + (rand(uv,3.0)-0.5)*0.0625;
    float blend = iTime / (iTime + 1.0);
    blend = min(blend,0.99);
    fragColor.r = blend*fragColor.r+step(delta,0.0)*2.0;

    //if (iMouse.z>0.0) {   fragColor.r += step( length(iMouse.xy-uv), 15.0)*20000000.0; }
    fragColor.gba =vec3( fragColor.r);



}
